import React from 'react';
import './GameInfo.css';

const GameInfo = ({ playerTurn, gameStatus, moveHistory, onResetGame, isInCheck }) => {
  // Group moves into pairs for display
  const moveGroups = [];
  for (let i = 0; i < moveHistory.length; i += 2) {
    moveGroups.push({
      number: Math.floor(i / 2) + 1,
      white: moveHistory[i],
      black: i + 1 < moveHistory.length ? moveHistory[i + 1] : null
    });
  }

  return (
    <div className="game-info">
      <div className="status-container">
        <h2>Game Status</h2>
        <div className="status">
          {gameStatus === 'ongoing' ? (
            <div>
              <p>Current Turn: <span className={playerTurn}>{playerTurn.charAt(0).toUpperCase() + playerTurn.slice(1)}</span></p>
              {isInCheck && <p className="check-status">Check!</p>}
            </div>
          ) : (
            <p className="game-over">{gameStatus}</p>
          )}
        </div>
        <button className="reset-button" onClick={onResetGame}>
          Reset Game
        </button>
      </div>

      <div className="move-history">
        <h2>Move History</h2>
        <div className="moves-container">
          {moveGroups.length === 0 ? (
            <p className="no-moves">No moves yet</p>
          ) : (
            <table className="moves-table">
              <thead>
                <tr>
                  <th>#</th>
                  <th>White</th>
                  <th>Black</th>
                </tr>
              </thead>
              <tbody>
                {moveGroups.map((group) => (
                  <tr key={group.number}>
                    <td>{group.number}.</td>
                    <td>{group.white ? group.white.notation : ''}</td>
                    <td>{group.black ? group.black.notation : ''}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>

      <div className="game-info-footer">
        <p>You play as White</p>
        <p>AI plays as Black</p>
        <p className="hint">Hint: Click on a piece to see valid moves</p>
      </div>
    </div>
  );
};

export default GameInfo;
