import React, { useState } from 'react';
import './GameInfo.css';

const GameInfo = ({ 
  playerTurn, 
  gameStatus, 
  moveHistory, 
  onResetGame, 
  isInCheck, 
  aiDifficulty,
  onGetHint
}) => {
  const [activeTab, setActiveTab] = useState('moves');

  // Group moves into pairs for display
  const moveGroups = [];
  for (let i = 0; i < moveHistory.length; i += 2) {
    moveGroups.push({
      number: Math.floor(i / 2) + 1,
      white: moveHistory[i],
      black: i + 1 < moveHistory.length ? moveHistory[i + 1] : null
    });
  }

  // Calculate material advantage
  const calculateMaterialAdvantage = () => {
    const pieceValues = {
      'pawn': 1,
      'knight': 3,
      'bishop': 3,
      'rook': 5,
      'queen': 9
    };

    let whiteMaterial = 0;
    let blackMaterial = 0;

    moveHistory.forEach(move => {
      if (move.capturedPiece) {
        const value = pieceValues[move.capturedPiece.type] || 0;
        if (move.color === 'white') {
          whiteMaterial += value;
        } else {
          blackMaterial += value;
        }
      }
    });

    const advantage = whiteMaterial - blackMaterial;

    if (advantage > 0) {
      return `+${advantage} White`;
    } else if (advantage < 0) {
      return `+${Math.abs(advantage)} Black`;
    } else {
      return 'Equal';
    }
  };

  const renderGameStatus = () => {
    return (
      <div className="status-container">
        <h2>Game Status</h2>
        <div className="status">
          {gameStatus === 'ongoing' ? (
            <div>
              <div className="turn-indicator">
                <div className={`player-indicator ${playerTurn === 'white' ? 'active' : ''}`}>
                  <span className="player-color white">White</span>
                  <span className="player-type">You</span>
                </div>
                <div className="vs">vs</div>
                <div className={`player-indicator ${playerTurn === 'black' ? 'active' : ''}`}>
                  <span className="player-color black">Black</span>
                  <span className="player-type">AI</span>
                </div>
              </div>
              <p className="current-turn">Current Turn: <span className={playerTurn}>{playerTurn.charAt(0).toUpperCase() + playerTurn.slice(1)}</span></p>
              {isInCheck && <p className="check-status">Check!</p>}
            </div>
          ) : (
            <p className="game-over">{gameStatus}</p>
          )}
        </div>
        <div className="game-controls">
          <button className="reset-button" onClick={onResetGame}>
            New Game
          </button>
          <button className="hint-button" onClick={onGetHint}>
            Hint
          </button>
        </div>
      </div>
    );
  };

  const renderMoveHistory = () => {
    return (
      <div className="move-history">
        <div className="tabs">
          <button 
            className={`tab ${activeTab === 'moves' ? 'active' : ''}`}
            onClick={() => setActiveTab('moves')}
          >
            Moves
          </button>
          <button 
            className={`tab ${activeTab === 'analysis' ? 'active' : ''}`}
            onClick={() => setActiveTab('analysis')}
          >
            Analysis
          </button>
        </div>

        {activeTab === 'moves' ? (
          <div className="moves-container">
            {moveGroups.length === 0 ? (
              <p className="no-moves">No moves yet</p>
            ) : (
              <table className="moves-table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>White</th>
                    <th>Black</th>
                  </tr>
                </thead>
                <tbody>
                  {moveGroups.map((group) => (
                    <tr key={group.number}>
                      <td>{group.number}.</td>
                      <td>{group.white ? group.white.notation : ''}</td>
                      <td>{group.black ? group.black.notation : ''}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            )}
          </div>
        ) : (
          <div className="analysis-container">
            <div className="analysis-item">
              <span className="analysis-label">Total Moves:</span>
              <span className="analysis-value">{moveHistory.length}</span>
            </div>
            <div className="analysis-item">
              <span className="analysis-label">Material:</span>
              <span className="analysis-value">{calculateMaterialAdvantage()}</span>
            </div>
            <div className="analysis-item">
              <span className="analysis-label">AI Difficulty:</span>
              <span className="analysis-value difficulty-badge">
                {aiDifficulty.charAt(0).toUpperCase() + aiDifficulty.slice(1)}
              </span>
            </div>
            <div className="analysis-item">
              <span className="analysis-label">AI Thinking Depth:</span>
              <span className="analysis-value">
                {aiDifficulty === 'easy' ? '1 ply' : 
                 aiDifficulty === 'medium' ? '2-3 ply' : 
                 '3-4 ply'}
              </span>
            </div>
            <div className="analysis-note">
              Higher difficulty means deeper search and stronger play
            </div>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="game-info">
      {renderGameStatus()}
      {renderMoveHistory()}

      <div className="game-info-footer">
        <p>You play as White</p>
        <p>AI plays as Black</p>
        <p className="hint">Hint: Click on a piece to see valid moves</p>
      </div>
    </div>
  );
};

export default GameInfo;
