import React, { useState, useEffect, useCallback } from 'react';
import './App.css';
import ChessBoard from './components/ChessBoard';
import GameInfo from './components/GameInfo';
import PromotionDialog from './components/PromotionDialog';
import ThemeSelector from './components/ThemeSelector';
import themes from './themes';
import { 
  initializeBoard, 
  isValidMove, 
  makeMove, 
  isInCheck, 
  isCheckmate, 
  isStalemate,
  isPawnPromotion
} from './utils/chessLogic';
import { findBestMove, getAIMove } from './utils/aiPlayer';

function App() {
  const [board, setBoard] = useState(initializeBoard());
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [playerTurn, setPlayerTurn] = useState('white');
  const [gameStatus, setGameStatus] = useState('ongoing');
  const [moveHistory, setMoveHistory] = useState([]);
  const [gameState, setGameState] = useState({
    enPassantTarget: null,
    castlingRights: {
      white: { kingSide: true, queenSide: true },
      black: { kingSide: true, queenSide: true }
    }
  });
  const [highlightedSquares, setHighlightedSquares] = useState([]);
  const [checkIndicator, setCheckIndicator] = useState(null);
  const [aiDifficulty, setAiDifficulty] = useState('medium'); // 'easy', 'medium', 'hard'
  const [showHint, setShowHint] = useState(false);
  const [hintMove, setHintMove] = useState(null);
  const [promotionDialog, setPromotionDialog] = useState(null);
  const [currentTheme, setCurrentTheme] = useState('classic');
  const [aiThinking, setAiThinking] = useState(false);

  // Check for check, checkmate, or stalemate after each move
  useEffect(() => {
    if (moveHistory.length > 0) {
      // Check if any king is in check
      if (isInCheck(board, 'white')) {
        setCheckIndicator('white');

        // Check if it's checkmate
        if (isCheckmate(board, 'white')) {
          setGameStatus('Checkmate! Black wins.');
        }
      } else if (isInCheck(board, 'black')) {
        setCheckIndicator('black');

        // Check if it's checkmate
        if (isCheckmate(board, 'black')) {
          setGameStatus('Checkmate! White wins.');
        }
      } else {
        setCheckIndicator(null);
      }

      // Check for stalemate
      if (playerTurn === 'white' && isStalemate(board, 'white')) {
        setGameStatus('Draw by stalemate');
      } else if (playerTurn === 'black' && isStalemate(board, 'black')) {
        setGameStatus('Draw by stalemate');
      }
    }
  }, [board, playerTurn, moveHistory]);

  // AI makes a move when it's black's turn
  useEffect(() => {
    if (playerTurn === 'black' && gameStatus === 'ongoing') {
      makeAIMove();
    }
  }, [playerTurn, gameStatus, makeAIMove]);

  // Calculate valid moves for the selected piece
  useEffect(() => {
    if (selectedPiece) {
      const validMoves = [];
      for (let row = 0; row < 8; row++) {
        for (let col = 0; col < 8; col++) {
          if (isValidMove(board, selectedPiece.row, selectedPiece.col, row, col, gameState)) {
            validMoves.push({ row, col });
          }
        }
      }
      setHighlightedSquares(validMoves);
    } else {
      setHighlightedSquares([]);
    }
  }, [selectedPiece, board, gameState]);

  // AI's turn
  useEffect(() => {
    if (playerTurn === 'black' && gameStatus === 'ongoing' && !promotionDialog) {
      // AI thinking time varies by difficulty
      const thinkingTimes = {
        'easy': [300, 700],
        'medium': [500, 1000],
        'hard': [800, 1500]
      };

      const [minTime, maxTime] = thinkingTimes[aiDifficulty];
      const aiThinkingTime = Math.random() * (maxTime - minTime) + minTime;

      const aiMoveTimeout = setTimeout(() => {
        // AI search depth varies by difficulty
        const searchDepth = {
          'easy': 1,
          'medium': 2,
          'hard': 3
        }[aiDifficulty];

        const aiMove = getAIMove(board, 'black', gameState, searchDepth);

        if (aiMove) {
          const { fromRow, fromCol, toRow, toCol } = aiMove;
          const aiPiece = board[fromRow][fromCol];

          // Check if this is a pawn promotion
          if (isPawnPromotion(aiPiece, toRow)) {
            // AI always promotes to queen
            const { board: newBoard, gameState: newGameState } = makeMove(
              board, 
              fromRow, 
              fromCol, 
              toRow, 
              toCol, 
              gameState,
              'queen'
            );

            // Create move notation
            const fromSquare = `${String.fromCharCode(97 + fromCol)}${8 - fromRow}`;
            const toSquare = `${String.fromCharCode(97 + toCol)}${8 - toRow}`;
            const captureSymbol = board[toRow][toCol] ? 'x' : '';

            let moveNotation = `${fromSquare}${captureSymbol}${toSquare}=Q`;

            // Check if the move results in check or checkmate
            if (isCheckmate(newBoard, 'white')) {
              moveNotation += '#';
            } else if (isInCheck(newBoard, 'white')) {
              moveNotation += '+';
            }

            // Update the game state
            setBoard(newBoard);
            setGameState(newGameState);
            setMoveHistory([...moveHistory, { 
              notation: moveNotation, 
              color: 'black',
              from: { row: fromRow, col: fromCol },
              to: { row: toRow, col: toCol },
              capturedPiece: board[toRow][toCol],
              promotion: 'queen'
            }]);
            setPlayerTurn('white');
            setShowHint(false);
            setHintMove(null);
          } else {
            // Regular move
            const { board: newBoard, gameState: newGameState } = makeMove(
              board, 
              fromRow, 
              fromCol, 
              toRow, 
              toCol, 
              gameState
            );

            // Create move notation
            const pieceSymbol = getPieceSymbol(aiPiece.type);
            const fromSquare = `${String.fromCharCode(97 + fromCol)}${8 - fromRow}`;
            const toSquare = `${String.fromCharCode(97 + toCol)}${8 - toRow}`;
            const captureSymbol = board[toRow][toCol] ? 'x' : '';

            let moveNotation = `${pieceSymbol}${fromSquare}${captureSymbol}${toSquare}`;

            // Check if the move results in check or checkmate
            if (isCheckmate(newBoard, 'white')) {
              moveNotation += '#';
            } else if (isInCheck(newBoard, 'white')) {
              moveNotation += '+';
            }

            // Update the game state
            setBoard(newBoard);
            setGameState(newGameState);
            setMoveHistory([...moveHistory, { 
              notation: moveNotation, 
              color: 'black',
              from: { row: fromRow, col: fromCol },
              to: { row: toRow, col: toCol },
              capturedPiece: board[toRow][toCol]
            }]);
            setPlayerTurn('white');
            setShowHint(false);
            setHintMove(null);
          }
        }
      }, aiThinkingTime);

      return () => clearTimeout(aiMoveTimeout);
    }
  }, [playerTurn, board, gameStatus, gameState, moveHistory, aiDifficulty, promotionDialog]);

  const getPieceSymbol = (pieceType) => {
    switch (pieceType) {
      case 'pawn': return '';
      case 'knight': return 'N';
      case 'bishop': return 'B';
      case 'rook': return 'R';
      case 'queen': return 'Q';
      case 'king': return 'K';
      default: return '';
    }
  };

  // Generate algebraic notation for a move
  const generateMoveNotation = (piece, fromRow, fromCol, toRow, toCol, capturedPiece, promotionPiece) => {
    const files = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
    const ranks = ['8', '7', '6', '5', '4', '3', '2', '1'];

    const fromSquare = files[fromCol] + ranks[fromRow];
    const toSquare = files[toCol] + ranks[toRow];

    let notation = '';

    // Add piece symbol (except for pawns)
    if (piece.type !== 'pawn') {
      notation += piece.type.charAt(0).toUpperCase();
    }

    // Add capture symbol if applicable
    if (capturedPiece) {
      if (piece.type === 'pawn') {
        notation += fromSquare.charAt(0);
      }
      notation += 'x';
    }

    // Add destination square
    notation += toSquare;

    // Add promotion piece if applicable
    if (promotionPiece) {
      notation += '=' + promotionPiece.charAt(0).toUpperCase();
    }

    return notation;
  };

  const handleSquareClick = (row, col) => {
    // If game is over or promotion dialog is open, don't allow any moves
    if (gameStatus !== 'ongoing' || promotionDialog) return;

    // If it's not the player's turn, don't allow any moves
    if (playerTurn !== 'white') return;

    const piece = board[row][col];

    // If a piece is already selected
    if (selectedPiece) {
      // If clicking on the same piece, deselect it
      if (selectedPiece.row === row && selectedPiece.col === col) {
        setSelectedPiece(null);
        return;
      }

      // If clicking on a different piece of the same color, select that piece instead
      if (piece && piece.color === playerTurn) {
        setSelectedPiece({ row, col, piece });
        return;
      }

      // Try to make a move
      if (isValidMove(board, selectedPiece.row, selectedPiece.col, row, col, gameState)) {
        // Check if this is a pawn promotion
        if (isPawnPromotion(selectedPiece.piece, row)) {
          // Show promotion dialog
          const boardRect = document.querySelector('.chess-board').getBoundingClientRect();
          const squareSize = boardRect.width / 8;

          setPromotionDialog({
            fromRow: selectedPiece.row,
            fromCol: selectedPiece.col,
            toRow: row,
            toCol: col,
            position: {
              x: boardRect.left + col * squareSize + squareSize / 2,
              y: boardRect.top + row * squareSize + squareSize / 2
            }
          });
          return;
        }

        // Regular move
        const { board: newBoard, gameState: newGameState } = makeMove(
          board, 
          selectedPiece.row, 
          selectedPiece.col, 
          row, 
          col, 
          gameState
        );

        // Create move notation
        const pieceSymbol = getPieceSymbol(selectedPiece.piece.type);
        const fromSquare = `${String.fromCharCode(97 + selectedPiece.col)}${8 - selectedPiece.row}`;
        const toSquare = `${String.fromCharCode(97 + col)}${8 - row}`;
        const captureSymbol = board[row][col] ? 'x' : '';

        let moveNotation = `${pieceSymbol}${fromSquare}${captureSymbol}${toSquare}`;

        // Check if the move results in check or checkmate
        if (isCheckmate(newBoard, 'black')) {
          moveNotation += '#';
        } else if (isInCheck(newBoard, 'black')) {
          moveNotation += '+';
        }

        // Update the game state
        setBoard(newBoard);
        setGameState(newGameState);
        setMoveHistory([...moveHistory, { 
          notation: moveNotation, 
          color: 'white',
          from: { row: selectedPiece.row, col: selectedPiece.col },
          to: { row, col },
          capturedPiece: board[row][col]
        }]);
        setSelectedPiece(null);
        setPlayerTurn('black');
        setShowHint(false);
        setHintMove(null);
      }
    } else {
      // If no piece is selected and clicked on a piece of the player's color, select it
      if (piece && piece.color === playerTurn) {
        setSelectedPiece({ row, col, piece });
      }
    }
  };

  const handlePromotion = (pieceType) => {
    if (!promotionDialog) return;

    const { fromRow, fromCol, toRow, toCol } = promotionDialog;

    // Make the move with promotion
    const { board: newBoard, gameState: newGameState } = makeMove(
      board, 
      fromRow, 
      fromCol, 
      toRow, 
      toCol, 
      gameState,
      pieceType
    );

    // Create move notation
    const fromSquare = `${String.fromCharCode(97 + fromCol)}${8 - fromRow}`;
    const toSquare = `${String.fromCharCode(97 + toCol)}${8 - toRow}`;
    const captureSymbol = board[toRow][toCol] ? 'x' : '';
    const promotionSymbol = pieceType === 'knight' ? 'N' : pieceType.charAt(0).toUpperCase();

    let moveNotation = `${fromSquare}${captureSymbol}${toSquare}=${promotionSymbol}`;

    // Check if the move results in check or checkmate
    if (isCheckmate(newBoard, 'black')) {
      moveNotation += '#';
    } else if (isInCheck(newBoard, 'black')) {
      moveNotation += '+';
    }

    // Update the game state
    setBoard(newBoard);
    setGameState(newGameState);
    setMoveHistory([...moveHistory, { 
      notation: moveNotation, 
      color: 'white',
      from: { row: fromRow, col: fromCol },
      to: { row: toRow, col: toCol },
      capturedPiece: board[toRow][toCol],
      promotion: pieceType
    }]);
    setSelectedPiece(null);
    setPlayerTurn('black');
    setPromotionDialog(null);
    setShowHint(false);
    setHintMove(null);
  };

  const resetGame = () => {
    setBoard(initializeBoard());
    setSelectedPiece(null);
    setPlayerTurn('white');
    setGameStatus('ongoing');
    setMoveHistory([]);
    setGameState({
      enPassantTarget: null,
      castlingRights: {
        white: { kingSide: true, queenSide: true },
        black: { kingSide: true, queenSide: true }
      }
    });
    setHighlightedSquares([]);
    setCheckIndicator(null);
    setShowHint(false);
    setHintMove(null);
    setPromotionDialog(null);
  };

  const handleDifficultyChange = (difficulty) => {
    setAiDifficulty(difficulty);
    resetGame();
  };

  const getHint = () => {
    // Use the AI to suggest a move for the player
    if (playerTurn === 'white' && gameStatus === 'ongoing') {
      const suggestedMove = getAIMove(board, 'white', gameState, 2);
      if (suggestedMove) {
        setShowHint(true);
        setHintMove(suggestedMove);
      }
    }
  };

  // Handle theme change
  const handleThemeChange = (themeKey) => {
    setCurrentTheme(themeKey);
    // Save theme preference to localStorage
    localStorage.setItem('chessTheme', themeKey);
  };

  // Load saved theme from localStorage on initial render
  useEffect(() => {
    const savedTheme = localStorage.getItem('chessTheme');
    if (savedTheme && themes[savedTheme]) {
      setCurrentTheme(savedTheme);
    }
  }, []);

  // Make AI move
  const makeAIMove = useCallback(() => {
    if (playerTurn === 'black' && gameStatus === 'ongoing') {
      setAiThinking(true);

      // Use setTimeout to allow the UI to update before starting the AI calculation
      setTimeout(() => {
        const aiMove = getAIMove(board, 'black', { lastMove: moveHistory[moveHistory.length - 1] }, aiDifficulty);

        if (aiMove) {
          const { fromRow, fromCol, toRow, toCol } = aiMove;

          // Check if this is a pawn promotion
          if (isPawnPromotion(board, fromRow, fromCol, toRow, toCol)) {
            // AI always promotes to queen
            handleMove(fromRow, fromCol, toRow, toCol, 'queen');
          } else {
            handleMove(fromRow, fromCol, toRow, toCol);
          }
        }

        setAiThinking(false);
      }, 100);
    }
  }, [board, playerTurn, gameStatus, moveHistory, aiDifficulty]);

  // Handle move
  const handleMove = (fromRow, fromCol, toRow, toCol, promotionPiece = null) => {
    const { board: newBoard, gameState: newGameState, capturedPiece } = makeMove(
      board, 
      fromRow, 
      fromCol, 
      toRow, 
      toCol, 
      { lastMove: moveHistory.length > 0 ? moveHistory[moveHistory.length - 1] : null },
      promotionPiece
    );

    setBoard(newBoard);

    // Add move to history
    const piece = board[fromRow][fromCol];
    const notation = generateMoveNotation(piece, fromRow, fromCol, toRow, toCol, capturedPiece, promotionPiece);

    const newMove = {
      from: { row: fromRow, col: fromCol },
      to: { row: toRow, col: toCol },
      piece,
      capturedPiece,
      notation,
      color: piece.color
    };

    setMoveHistory([...moveHistory, newMove]);

    // Switch turns
    setPlayerTurn(playerTurn === 'white' ? 'black' : 'white');

    // Clear selection and highlights
    setSelectedPiece(null);
    setHighlightedSquares([]);
    setShowHint(false);
    setHintMove(null);
  };

  return (
    <div className="app">
      <div className="app-header">
        <h1>AI Chess</h1>
        <div className="author-credits">
          <span>Created by Aditya Thakkar</span>
        </div>
      </div>
      <div className="difficulty-selector">
        <span className="difficulty-label">AI Difficulty:</span>
        <div className="difficulty-buttons">
          <button 
            className={`difficulty-button ${aiDifficulty === 'easy' ? 'active' : ''}`}
            onClick={() => handleDifficultyChange('easy')}
          >
            Easy
          </button>
          <button 
            className={`difficulty-button ${aiDifficulty === 'medium' ? 'active' : ''}`}
            onClick={() => handleDifficultyChange('medium')}
          >
            Medium
          </button>
          <button 
            className={`difficulty-button ${aiDifficulty === 'hard' ? 'active' : ''}`}
            onClick={() => handleDifficultyChange('hard')}
          >
            Hard
          </button>
        </div>
      </div>
      <ThemeSelector 
        currentTheme={currentTheme} 
        onThemeChange={handleThemeChange} 
      />
      <div className="game-container">
        <ChessBoard 
          board={board} 
          selectedPiece={selectedPiece} 
          onSquareClick={handleSquareClick}
          highlightedSquares={highlightedSquares}
          checkIndicator={checkIndicator}
          lastMove={moveHistory.length > 0 ? moveHistory[moveHistory.length - 1] : null}
          hintMove={showHint ? hintMove : null}
          theme={themes[currentTheme]}
        />
        <GameInfo 
          playerTurn={playerTurn} 
          gameStatus={gameStatus} 
          moveHistory={moveHistory} 
          onResetGame={resetGame} 
          isInCheck={checkIndicator !== null}
          aiDifficulty={aiDifficulty}
          onGetHint={getHint}
          aiThinking={aiThinking}
        />
      </div>
      {promotionDialog && (
        <PromotionDialog 
          position={promotionDialog.position}
          color="white"
          onSelect={handlePromotion}
          onCancel={() => setPromotionDialog(null)}
        />
      )}
      <div className="app-footer">
        <div className="footer-content">
          <p>Chess AI with Minimax Algorithm & Alpha-Beta Pruning</p>
          <p>© {new Date().getFullYear()} AI Chess by Aditya Thakkar</p>
        </div>
      </div>
    </div>
  );
}

export default App;
