import React, { useState, useEffect } from 'react';
import './App.css';
import ChessBoard from './components/ChessBoard';
import GameInfo from './components/GameInfo';
import { 
  initializeBoard, 
  isValidMove, 
  makeMove, 
  getAIMove, 
  isInCheck, 
  isCheckmate, 
  isStalemate 
} from './utils/chessLogic';

function App() {
  const [board, setBoard] = useState(initializeBoard());
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [playerTurn, setPlayerTurn] = useState('white');
  const [gameStatus, setGameStatus] = useState('ongoing');
  const [moveHistory, setMoveHistory] = useState([]);
  const [gameState, setGameState] = useState({
    enPassantTarget: null,
    castlingRights: {
      white: { kingSide: true, queenSide: true },
      black: { kingSide: true, queenSide: true }
    }
  });
  const [highlightedSquares, setHighlightedSquares] = useState([]);
  const [checkIndicator, setCheckIndicator] = useState(null);
  const [aiDifficulty, setAiDifficulty] = useState('medium'); // 'easy', 'medium', 'hard'
  const [showHint, setShowHint] = useState(false);
  const [hintMove, setHintMove] = useState(null);
  // const [promotionDialog, setPromotionDialog] = useState(null);

  // Check for check, checkmate, or stalemate after each move
  useEffect(() => {
    if (gameStatus !== 'ongoing') return;

    const currentPlayer = playerTurn;
    const opponent = currentPlayer === 'white' ? 'black' : 'white';

    // Check if the current player is in check
    if (isInCheck(board, currentPlayer)) {
      setCheckIndicator(currentPlayer);

      // Check if it's checkmate
      if (isCheckmate(board, currentPlayer)) {
        setGameStatus(`${opponent} wins by checkmate`);
        return;
      }
    } else {
      setCheckIndicator(null);

      // Check if it's stalemate
      if (isStalemate(board, currentPlayer)) {
        setGameStatus('draw by stalemate');
        return;
      }
    }
  }, [board, playerTurn, gameStatus]);

  // Calculate valid moves for the selected piece
  useEffect(() => {
    if (selectedPiece) {
      const validMoves = [];
      for (let row = 0; row < 8; row++) {
        for (let col = 0; col < 8; col++) {
          if (isValidMove(board, selectedPiece.row, selectedPiece.col, row, col, gameState)) {
            validMoves.push({ row, col });
          }
        }
      }
      setHighlightedSquares(validMoves);
    } else {
      setHighlightedSquares([]);
    }
  }, [selectedPiece, board, gameState]);

  // AI's turn
  useEffect(() => {
    if (playerTurn === 'black' && gameStatus === 'ongoing') {
      // AI thinking time varies by difficulty
      const thinkingTimes = {
        'easy': [300, 700],
        'medium': [500, 1000],
        'hard': [800, 1500]
      };

      const [minTime, maxTime] = thinkingTimes[aiDifficulty];
      const aiThinkingTime = Math.random() * (maxTime - minTime) + minTime;

      const aiMoveTimeout = setTimeout(() => {
        // AI search depth varies by difficulty
        const searchDepth = {
          'easy': 1,
          'medium': 2,
          'hard': 3
        }[aiDifficulty];

        const aiMove = getAIMove(board, 'black', gameState, searchDepth);

        if (aiMove) {
          const { fromRow, fromCol, toRow, toCol } = aiMove;
          const aiPiece = board[fromRow][fromCol];

          // Make the AI move
          const { board: newBoard, gameState: newGameState } = makeMove(
            board, 
            fromRow, 
            fromCol, 
            toRow, 
            toCol, 
            gameState
          );

          // Create move notation
          const pieceSymbol = getPieceSymbol(aiPiece.type);
          const fromSquare = `${String.fromCharCode(97 + fromCol)}${8 - fromRow}`;
          const toSquare = `${String.fromCharCode(97 + toCol)}${8 - toRow}`;
          const captureSymbol = board[toRow][toCol] ? 'x' : '';

          let moveNotation = `${pieceSymbol}${fromSquare}${captureSymbol}${toSquare}`;

          // Check if the move results in check or checkmate
          if (isCheckmate(newBoard, 'white')) {
            moveNotation += '#';
          } else if (isInCheck(newBoard, 'white')) {
            moveNotation += '+';
          }

          // Update the game state
          setBoard(newBoard);
          setGameState(newGameState);
          setMoveHistory([...moveHistory, { 
            notation: moveNotation, 
            color: 'black',
            from: { row: fromRow, col: fromCol },
            to: { row: toRow, col: toCol },
            capturedPiece: board[toRow][toCol]
          }]);
          setPlayerTurn('white');
          setShowHint(false);
          setHintMove(null);
        }
      }, aiThinkingTime);

      return () => clearTimeout(aiMoveTimeout);
    }
  }, [playerTurn, board, gameStatus, gameState, moveHistory, aiDifficulty]);

  const getPieceSymbol = (pieceType) => {
    switch (pieceType) {
      case 'pawn': return '';
      case 'knight': return 'N';
      case 'bishop': return 'B';
      case 'rook': return 'R';
      case 'queen': return 'Q';
      case 'king': return 'K';
      default: return '';
    }
  };

  const handleSquareClick = (row, col) => {
    // If game is over, don't allow any moves
    if (gameStatus !== 'ongoing') return;

    // If it's not the player's turn, don't allow any moves
    if (playerTurn !== 'white') return;

    const piece = board[row][col];

    // If a piece is already selected
    if (selectedPiece) {
      // If clicking on the same piece, deselect it
      if (selectedPiece.row === row && selectedPiece.col === col) {
        setSelectedPiece(null);
        return;
      }

      // If clicking on a different piece of the same color, select that piece instead
      if (piece && piece.color === playerTurn) {
        setSelectedPiece({ row, col, piece });
        return;
      }

      // Try to make a move
      if (isValidMove(board, selectedPiece.row, selectedPiece.col, row, col, gameState)) {
        const { board: newBoard, gameState: newGameState } = makeMove(
          board, 
          selectedPiece.row, 
          selectedPiece.col, 
          row, 
          col, 
          gameState
        );

        // Create move notation
        const pieceSymbol = getPieceSymbol(selectedPiece.piece.type);
        const fromSquare = `${String.fromCharCode(97 + selectedPiece.col)}${8 - selectedPiece.row}`;
        const toSquare = `${String.fromCharCode(97 + col)}${8 - row}`;
        const captureSymbol = board[row][col] ? 'x' : '';

        let moveNotation = `${pieceSymbol}${fromSquare}${captureSymbol}${toSquare}`;

        // Check if the move results in check or checkmate
        if (isCheckmate(newBoard, 'black')) {
          moveNotation += '#';
        } else if (isInCheck(newBoard, 'black')) {
          moveNotation += '+';
        }

        // Update the game state
        setBoard(newBoard);
        setGameState(newGameState);
        setMoveHistory([...moveHistory, { 
          notation: moveNotation, 
          color: 'white',
          from: { row: selectedPiece.row, col: selectedPiece.col },
          to: { row, col },
          capturedPiece: board[row][col]
        }]);
        setSelectedPiece(null);
        setPlayerTurn('black');
        setShowHint(false);
        setHintMove(null);
      }
    } else {
      // If no piece is selected and clicked on a piece of the player's color, select it
      if (piece && piece.color === playerTurn) {
        setSelectedPiece({ row, col, piece });
      }
    }
  };

  const resetGame = () => {
    setBoard(initializeBoard());
    setSelectedPiece(null);
    setPlayerTurn('white');
    setGameStatus('ongoing');
    setMoveHistory([]);
    setGameState({
      enPassantTarget: null,
      castlingRights: {
        white: { kingSide: true, queenSide: true },
        black: { kingSide: true, queenSide: true }
      }
    });
    setHighlightedSquares([]);
    setCheckIndicator(null);
    setShowHint(false);
    setHintMove(null);
  };

  const handleDifficultyChange = (difficulty) => {
    setAiDifficulty(difficulty);
    // Optionally reset the game when difficulty changes
    if (window.confirm('Change AI difficulty? Current game will be reset.')) {
      resetGame();
    }
  };

  const getHint = () => {
    // Use the AI to suggest a move for the player
    if (playerTurn === 'white' && gameStatus === 'ongoing') {
      const suggestedMove = getAIMove(board, 'white', gameState, 2);
      if (suggestedMove) {
        setShowHint(true);
        setHintMove(suggestedMove);
      }
    }
  };

  return (
    <div className="app">
      <h1>AI Chess</h1>
      <div className="difficulty-selector">
        <span className="difficulty-label">AI Difficulty:</span>
        <div className="difficulty-buttons">
          <button 
            className={`difficulty-button ${aiDifficulty === 'easy' ? 'active' : ''}`}
            onClick={() => handleDifficultyChange('easy')}
          >
            Easy
          </button>
          <button 
            className={`difficulty-button ${aiDifficulty === 'medium' ? 'active' : ''}`}
            onClick={() => handleDifficultyChange('medium')}
          >
            Medium
          </button>
          <button 
            className={`difficulty-button ${aiDifficulty === 'hard' ? 'active' : ''}`}
            onClick={() => handleDifficultyChange('hard')}
          >
            Hard
          </button>
        </div>
      </div>
      <div className="game-container">
        <ChessBoard 
          board={board} 
          selectedPiece={selectedPiece} 
          onSquareClick={handleSquareClick}
          highlightedSquares={highlightedSquares}
          checkIndicator={checkIndicator}
          lastMove={moveHistory.length > 0 ? moveHistory[moveHistory.length - 1] : null}
          hintMove={showHint ? hintMove : null}
        />
        <GameInfo 
          playerTurn={playerTurn} 
          gameStatus={gameStatus} 
          moveHistory={moveHistory} 
          onResetGame={resetGame} 
          isInCheck={checkIndicator !== null}
          aiDifficulty={aiDifficulty}
          onGetHint={getHint}
        />
      </div>
      <div className="app-footer">
        <p>Chess AI with Minimax Algorithm & Alpha-Beta Pruning</p>
        <p>Â© {new Date().getFullYear()} AI Chess</p>
      </div>
    </div>
  );
}

export default App;
