// Minimax algorithm with alpha-beta pruning
const minimax = (board, depth, alpha, beta, maximizingPlayer, color, gameState) => {
  // Base case: depth reached or game over
  if (depth === 0 || isCheckmate(board, 'white') || isCheckmate(board, 'black') || 
      isStalemate(board, 'white') || isStalemate(board, 'black')) {
    return evaluateBoard(board, color);
  }

  const currentColor = maximizingPlayer ? color : (color === 'white' ? 'black' : 'white');
  const moves = generateMoves(board, currentColor, gameState);

  if (maximizingPlayer) {
    let maxEval = -Infinity;

