    return maxEval;
  } else {
    let minEval = Infinity;

    for (const move of moves) {
      const { fromRow, fromCol, toRow, toCol } = move;
      const { board: newBoard, gameState: newGameState } = makeMove(board, fromRow, fromCol, toRow, toCol, gameState);

      const evaluation = minimax(newBoard, depth - 1, alpha, beta, true, color, newGameState);
      minEval = Math.min(minEval, evaluation);

      beta = Math.min(beta, evaluation);
      if (beta <= alpha) {
        break; // Alpha cutoff
      }
    }

    return minEval;
  }
};

// Find the best move using minimax
export const findBestMove = (board, color, gameState, depth = 3) => {
  let bestMove = null;
  let bestValue = color === 'white' ? -Infinity : Infinity;

  const moves = generateMoves(board, color, gameState);

  // Shuffle moves for variety
  for (let i = moves.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [moves[i], moves[j]] = [moves[j], moves[i]];
  }

  for (const move of moves) {
    const { fromRow, fromCol, toRow, toCol } = move;
    const { board: newBoard, gameState: newGameState } = makeMove(board, fromRow, fromCol, toRow, toCol, gameState);

    const value = minimax(
      newBoard, 
      depth - 1, 
      -Infinity, 
      Infinity, 
      color === 'black', // If black, next player is white (maximizing)
      color, 
      newGameState
    );

    if ((color === 'white' && value > bestValue) || (color === 'black' && value < bestValue)) {
      bestValue = value;
      bestMove = move;
    }
  }

  return bestMove;
