import React from 'react';
import './ChessBoard.css';

// Import SVG pieces
import whitePawn from '../assets/pieces/white-pawn.svg';
import whiteRook from '../assets/pieces/white-rook.svg';
import whiteKnight from '../assets/pieces/white-knight.svg';
import whiteBishop from '../assets/pieces/white-bishop.svg';
import whiteQueen from '../assets/pieces/white-queen.svg';
import whiteKing from '../assets/pieces/white-king.svg';
import blackPawn from '../assets/pieces/black-pawn.svg';
import blackRook from '../assets/pieces/black-rook.svg';
import blackKnight from '../assets/pieces/black-knight.svg';
import blackBishop from '../assets/pieces/black-bishop.svg';
import blackQueen from '../assets/pieces/black-queen.svg';
import blackKing from '../assets/pieces/black-king.svg';

const ChessBoard = ({ 
  board, 
  selectedPiece, 
  onSquareClick, 
  highlightedSquares, 
  checkIndicator,
  lastMove,
  hintMove,
  theme
}) => {
  // Map piece types to SVG images
  const pieceImages = {
    'white': {
      'pawn': whitePawn,
      'rook': whiteRook,
      'knight': whiteKnight,
      'bishop': whiteBishop,
      'queen': whiteQueen,
      'king': whiteKing
    },
    'black': {
      'pawn': blackPawn,
      'rook': blackRook,
      'knight': blackKnight,
      'bishop': blackBishop,
      'queen': blackQueen,
      'king': blackKing
    }
  };

  // Track captured pieces
  const capturedPieces = {
    white: [],
    black: []
  };

  // Count captured pieces
  board.flat().forEach(piece => {
    if (piece && piece.captured) {
      capturedPieces[piece.color].push(piece);
    }
  });

  // Check if a square is highlighted (valid move)
  const isHighlighted = (row, col) => {
    return highlightedSquares.some(square => square.row === row && square.col === col);
  };

  // Check if a square is part of the last move
  const isLastMove = (row, col) => {
    if (!lastMove) return false;

    const { from, to } = lastMove;
    return (from.row === row && from.col === col) || (to.row === row && to.col === col);
  };

  // Check if a square is part of a hint
  const isHintSquare = (row, col) => {
    if (!hintMove) return false;

    const { fromRow, fromCol, toRow, toCol } = hintMove;
    return (fromRow === row && fromCol === col) || (toRow === row && toCol === col);
  };

  // Render the board
  const renderBoard = () => {
    const rows = [];

    for (let row = 0; row < 8; row++) {
      const boardRow = [];

      for (let col = 0; col < 8; col++) {
        const piece = board[row][col];
        const isLight = (row + col) % 2 === 0;
        const isSelected = selectedPiece && selectedPiece.row === row && selectedPiece.col === col;
        const isCheck = checkIndicator && piece && piece.type === 'king' && piece.color === checkIndicator;

        let squareClassName = `square ${isLight ? 'light' : 'dark'}`;
        if (isSelected) squareClassName += ' selected';
        if (isHighlighted(row, col)) squareClassName += ' highlighted';
        if (isCheck) squareClassName += ' check';
        if (isLastMove(row, col)) squareClassName += ' last-move';
        if (isHintSquare(row, col)) squareClassName += ' hint';

        const squareStyle = {
          backgroundColor: isLight ? theme.lightSquare : theme.darkSquare
        };

        boardRow.push(
          <div 
            key={`${row}-${col}`} 
            className={squareClassName}
            style={squareStyle}
            onClick={() => onSquareClick(row, col)}
          >
            {piece && !piece.captured && (
              <img 
                src={pieceImages[piece.color][piece.type]} 
                alt={`${piece.color} ${piece.type}`} 
                className={`piece ${piece.color}`}
              />
            )}
          </div>
        );
      }

      rows.push(boardRow);
    }

    return rows;
  };

  // Render the board coordinates
  const renderCoordinates = () => {
    const files = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
    const ranks = ['8', '7', '6', '5', '4', '3', '2', '1'];

    return (
      <div className="board-coordinates">
        <div className="files">
          {files.map(file => (
            <div key={file}>{file}</div>
          ))}
        </div>
        <div className="ranks">
          {ranks.map(rank => (
            <div key={rank}>{rank}</div>
          ))}
        </div>
      </div>
    );
  };

  // Render captured pieces
  const renderCapturedPieces = (color) => {
    const capturedByOpponent = color === 'white' ? capturedPieces.black : capturedPieces.white;

    if (capturedByOpponent.length === 0) return null;

    return (
      <div className="captured-row">
        {capturedByOpponent.map((piece, index) => (
          <img 
            key={index}
            src={pieceImages[piece.color][piece.type]} 
            alt={`${piece.color} ${piece.type}`} 
            className="captured-piece"
          />
        ))}
      </div>
    );
  };

  // Apply theme styles
  const containerStyle = {
    background: theme.boardBg
  };

  // Apply theme-specific CSS variables
  const cssVariables = {
    '--highlight-color': theme.highlightColor,
    '--check-color': theme.checkColor,
    '--last-move-color': theme.lastMoveColor,
    '--accent-color': theme.accentColor
  };

  return (
    <div className="chess-board-container" style={{...containerStyle, ...cssVariables}}>
      <div className="chess-board">
        {renderBoard().map((row, index) => (
          <React.Fragment key={index}>
            {row}
          </React.Fragment>
        ))}
      </div>
      {renderCoordinates()}
      <div className="captured-pieces">
        {renderCapturedPieces('white')}
        {renderCapturedPieces('black')}
      </div>
    </div>
  );
};

export default ChessBoard;
