import React, { useState, useEffect } from 'react';
import './ChessBoard.css';

const ChessBoard = ({ 
  board, 
  selectedPiece, 
  onSquareClick, 
  highlightedSquares = [], 
  checkIndicator = null,
  lastMove = null,
  hintMove = null
}) => {
  const [capturedPieces, setCapturedPieces] = useState({
    white: [],
    black: []
  });

  // Track captured pieces
  useEffect(() => {
    const whitePieces = [];
    const blackPieces = [];

    // Count all pieces that should be on the board
    const expectedPieces = {
      'pawn': 8,
      'rook': 2,
      'knight': 2,
      'bishop': 2,
      'queen': 1,
      'king': 1
    };

    // Count pieces actually on the board
    const actualPieces = {
      'white': { 'pawn': 0, 'rook': 0, 'knight': 0, 'bishop': 0, 'queen': 0, 'king': 0 },
      'black': { 'pawn': 0, 'rook': 0, 'knight': 0, 'bishop': 0, 'queen': 0, 'king': 0 }
    };

    // Count pieces on the board
    for (let row = 0; row < 8; row++) {
      for (let col = 0; col < 8; col++) {
        const piece = board[row][col];
        if (piece) {
          actualPieces[piece.color][piece.type]++;
        }
      }
    }

    // Calculate captured pieces
    for (const pieceType in expectedPieces) {
      const whiteCaptured = expectedPieces[pieceType] - actualPieces['white'][pieceType];
      const blackCaptured = expectedPieces[pieceType] - actualPieces['black'][pieceType];

      for (let i = 0; i < whiteCaptured; i++) {
        whitePieces.push(pieceType);
      }

      for (let i = 0; i < blackCaptured; i++) {
        blackPieces.push(pieceType);
      }
    }

    setCapturedPieces({
      white: whitePieces,
      black: blackPieces
    });
  }, [board]);

  const renderSquare = (row, col) => {
    const piece = board[row][col];
    const isSelected = selectedPiece && selectedPiece.row === row && selectedPiece.col === col;
    const isLightSquare = (row + col) % 2 === 0;

    // Check if this square is a valid move destination
    const isValidMove = highlightedSquares.some(square => square.row === row && square.col === col);

    // Check if this square contains a king in check
    const isCheck = checkIndicator && piece && piece.type === 'king' && piece.color === checkIndicator;

    // Check if this square was part of the last move
    const isLastMoveFrom = lastMove && lastMove.from && lastMove.from.row === row && lastMove.from.col === col;
    const isLastMoveTo = lastMove && lastMove.to && lastMove.to.row === row && lastMove.to.col === col;

    // Check if this square is part of the hint move
    const isHintFrom = hintMove && hintMove.fromRow === row && hintMove.fromCol === col;
    const isHintTo = hintMove && hintMove.toRow === row && hintMove.toCol === col;

    // Determine piece symbol based on type and color
    let pieceSymbol = '';
    if (piece) {
      switch (piece.type) {
        case 'pawn':
          pieceSymbol = piece.color === 'white' ? '♙' : '♟';
          break;
        case 'rook':
          pieceSymbol = piece.color === 'white' ? '♖' : '♜';
          break;
        case 'knight':
          pieceSymbol = piece.color === 'white' ? '♘' : '♞';
          break;
        case 'bishop':
          pieceSymbol = piece.color === 'white' ? '♗' : '♝';
          break;
        case 'queen':
          pieceSymbol = piece.color === 'white' ? '♕' : '♛';
          break;
        case 'king':
          pieceSymbol = piece.color === 'white' ? '♔' : '♚';
          break;
        default:
          pieceSymbol = '';
      }
    }

    return (
      <div 
        key={`${row}-${col}`}
        className={`
          square 
          ${isLightSquare ? 'light' : 'dark'} 
          ${isSelected ? 'selected' : ''} 
          ${isValidMove ? 'valid-move' : ''}
          ${isCheck ? 'check' : ''}
          ${isLastMoveFrom ? 'last-move-from' : ''}
          ${isLastMoveTo ? 'last-move-to' : ''}
          ${isHintFrom ? 'hint-from' : ''}
          ${isHintTo ? 'hint-to' : ''}
        `}
        onClick={() => onSquareClick(row, col)}
      >
        {isValidMove && !piece && <div className="move-indicator"></div>}
        {piece && <div className={`piece ${piece.color}`}>{pieceSymbol}</div>}
        {isHintFrom && <div className="hint-indicator from"></div>}
        {isHintTo && <div className="hint-indicator to"></div>}
        {row === 7 && <div className="file-label">{String.fromCharCode(97 + col)}</div>}
        {col === 0 && <div className="rank-label">{8 - row}</div>}
      </div>
    );
  };

  const renderBoard = () => {
    const rows = [];
    for (let row = 0; row < 8; row++) {
      const cols = [];
      for (let col = 0; col < 8; col++) {
        cols.push(renderSquare(row, col));
      }
      rows.push(
        <div key={row} className="board-row">
          {cols}
        </div>
      );
    }
    return rows;
  };

  const renderCapturedPieces = (color) => {
    return capturedPieces[color].map((pieceType, index) => {
      let pieceSymbol = '';
      switch (pieceType) {
        case 'pawn':
          pieceSymbol = color === 'white' ? '♙' : '♟';
          break;
        case 'rook':
          pieceSymbol = color === 'white' ? '♖' : '♜';
          break;
        case 'knight':
          pieceSymbol = color === 'white' ? '♘' : '♞';
          break;
        case 'bishop':
          pieceSymbol = color === 'white' ? '♗' : '♝';
          break;
        case 'queen':
          pieceSymbol = color === 'white' ? '♕' : '♛';
          break;
        case 'king':
          pieceSymbol = color === 'white' ? '♔' : '♚';
          break;
        default:
          pieceSymbol = '';
      }

      return (
        <div key={`${color}-${pieceType}-${index}`} className={`captured-piece ${color}`}>
          {pieceSymbol}
        </div>
      );
    });
  };

  return (
    <div className="chess-board-container">
      <div className="captured-pieces black-captured">
        {renderCapturedPieces('black')}
      </div>
      <div className="chess-board">
        {renderBoard()}
      </div>
      <div className="captured-pieces white-captured">
        {renderCapturedPieces('white')}
      </div>
      <div className="board-coordinates">
        <div className="files">
          {['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'].map(file => (
            <div key={file} className="coordinate">{file}</div>
          ))}
        </div>
        <div className="ranks">
          {[8, 7, 6, 5, 4, 3, 2, 1].map(rank => (
            <div key={rank} className="coordinate">{rank}</div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ChessBoard;
